int maxScoreSightseeingPair2(vector<int>& A) {
    int max_inc = A[0] - 1, res = 0;
    for (int i = 1; i < A.size(); ++i, max_inc--) {
        res = max(res, max_inc + A[i]);
        max_inc = max(A[i], max_inc);
    }

    return res;
}
