```c++
    class Node {
    public:
        int val;
        Node *prev;
        Node *next;
        Node *child;

        Node() {}

        Node(int _val, Node *_prev, Node *_next, Node *_child) {
            val = _val;
            prev = _prev;
            next = _next;
            child = _child;
        }
    };

    Node *flatten(Node *head) {
        Node *cur = head;
        while (cur) {
            if (cur->child) {
                Node *next = cur->next;
                cur->next = cur->child;
                cur->child = nullptr;
                cur->next->prev = cur;

                // iter the children 
                Node *p = cur->next;
                while (p->next) p = p->next;

                p->next = next;
                if (next) next->prev = p;
            }
            cur = cur->next;
        }

        return head;
    }
```

